
cipher = ""
cipher_shift = ""


for i in range(len(cipher)):
    cipher_shift = " " + cipher_shift
    cipher_shift = cipher_shift[:-1]

    coincidences = zip(cipher, cipher_shift)

    counter = 0
    for c, s in coincidences:
        if c == s:
            counter += 1
        else:
            pass
    print(str(counter))
